package COMP321;

import java.util.Scanner;

public class timeloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input = new Scanner(System.in);
		int n = input.nextInt();
		for (int i=0; i< n; i++) {
			System.out.println((i+1) + " " + "Abracadabra");
		}
	}

}
